import '../assets/css/utils.css'
import Home from './HomePage'
import Header from './Header'
import React, { useState, useEffect } from 'react';

function App() {
  const [dollar, insertDollar] = useState(0);
  const [randomNumber, getRandomNumber] = useState([]);
  const [num, setNum] = useState([]);
  const [wonGame, checkGameStatus] = useState("");

  const Ifnum = (e) => {
    let number = e.target.defaultValue;
    // alert(number);
    let newNumbers;
    if (num.includes(number)) {
      newNumbers = num.filter(function (value) {
        return value != number;
      });
    }
    else {
      if (num.length >= 5) {
        alert("Oops!! You cannot add more than 5 numbers");
        return;
      }
      newNumbers = [...num, number]
    }
    //  alert(newNumbers);
    setNum(newNumbers);
  }

  const totalPrice = (e) => {
    if (num.length < 5) {
      alert("Oops!! You cannot select money before selecting all the 5 numbers!");
      return;
    }
    let count = e.target.id.split("_")[1];
    const total = parseInt(dollar) + parseInt(count);
    insertDollar(total);
  }
  const Clear = () => {
    const resetNum = [];
    setNum(resetNum);
    const newRandonNumbers = [];
    getRandomNumber(newRandonNumbers);
    const resetDollar = 0;
    insertDollar(resetDollar);
    const newGameStatus = "";
    checkGameStatus(newGameStatus);
  }
  const cash = () => {

    if (dollar == 0) {
      alert("Please select some cash value!!");
    }
    else if (num.length == 0) {
      alert("Please select all 5 numbers first");
    }
    else {
      alert("you have won $" + (parseInt(dollar) + parseInt(dollar)) + " Congratulations");

    }

  }
  const random = () => {
    alert("random");
    //setNum([]);
    let arr = [];
    while (arr.length < 5) {
      let r = Math.floor(Math.random() * 20) + 1;
      if (arr.indexOf(r) === -1) arr.push(r);
      alert(r);
    }
  }

  return (
    <div className="App">
      <Header />
      <Home Ifnum={Ifnum} num={num} totalPrice={totalPrice} dollar={dollar} Clear={Clear} cash={cash} random={random}/>
    </div>
  );
}

export default App;
