import '../assets/css/utils.css'
import Home from './HomePage'
import Header from './Header'
import React, { useState } from 'react';

function App() {
  const [money, addMoney] = useState(0);
  const [randomNumber, getRandomNumber] = useState([]);
  const [num, setNum] = useState([]);
  const [wonGame, checkGameStatus] = useState("");

  const Ifnum = (e) => {
    let number = e.target.defaultValue;
    alert(number);
    let newNumbers;
    // alert(event.target.defaultValue);
    if (num.includes(number)) {
      newNumbers = num.filter(function (value) {
        return value != number;
      });
    }
    else {
      if (num.length >= 5) {
        alert("Oops!! You cannot add more than 5 numbers");
        return;
      }
      newNumbers = [...num, number]
    }
    alert(newNumbers);
    setNum(newNumbers);
  }
  return (
    <div className="App">
      <Header />
      <Home Ifnum={Ifnum} />
    </div>
  );
}

export default App;
